#!/usr/bin/env ruby

DATA.each do |str|
  sum = 0
  pos = 0
  str.each_char do |char|
    pos += 1
    sum += case char
             when '('
               1
             when ')'
               -1
             else
               0
             end
    p pos if sum < 0
  end
  p sum
end

